import { createBoard } from '@wixc3/react-board';
import React, { useState, useCallback } from 'react';
import { Alert, Button } from '@blueprintjs/core';

// board cover
import cover from '../../../../assets/alert/alert-with-icon.svg';

export default createBoard({
    name: 'Alert with icon',
    Board: () => {
        const [isOpen, setIsOpen] = useState(false);
        const onClose = useCallback(() => setIsOpen(false), [setIsOpen]);

        return (
            <>
                <Button
                    onClick={() => setIsOpen(true)}
                    text="Open file deletion alert"
                />
                <Alert
                    confirmButtonText="Move to trash"
                    loading={false}
                    onClose={onClose}
                    cancelButtonText={'Cancel'}
                    icon={'trash'}
                    intent="danger"
                    isOpen={isOpen}
                >
                    <p>
                        Are you sure you want to move filename to Trash? You
                        will be able to restore it later, but it will become
                        private to you.
                    </p>
                </Alert>
            </>
        );
    },
    tags: ['alert', 'error', 'danger', 'icon'],
    cover,
});
