import { createBoard } from '@wixc3/react-board';
import { Tag } from '@blueprintjs/core';

// board cover
import cover from '../../../../assets/tags/tags-with-icon.svg';

const INITIAL_TAGS = ['Seattle', 'London', 'New York'];

export default createBoard({
    name: 'Tags with Icon',
    Board: () => (
        <>
            {INITIAL_TAGS.map((item) => (
                <Tag icon="home">{item}</Tag>
            ))}
        </>
    ),
    tags: ['tag', 'badge'],
    cover,
});
