import { createBoard } from '@wixc3/react-board';
import { Button, OverlayToaster, Toaster } from '@blueprintjs/core';

// board cover
import cover from '../../../../assets/toast/success.svg';

const toaster: Toaster = OverlayToaster.create({
    autoFocus: true,
    canEscapeKeyClear: true,
    maxToasts: 3,
    position: 'top',
    usePortal: true,
});

export default createBoard({
    name: 'Success',
    Board: () => {
        return (
            <Button
                intent="success"
                onClick={() =>
                    toaster.show({
                        action: { text: 'Undo' },
                        icon: 'tick',
                        intent: 'success',
                        message: 'Moved 6 files.',
                    })
                }
            >
                Toast
            </Button>
        );
    },
    tags: ['success', 'toast'],
    cover,
});
