import { createBoard } from '@wixc3/react-board';
import { Button, OverlayToaster, Toaster } from '@blueprintjs/core';

// board cover
import cover from '../../../../assets/toast/warning.svg';

const toaster: Toaster = OverlayToaster.create({
    autoFocus: true,
    canEscapeKeyClear: true,
    maxToasts: 3,
    position: 'top',
    usePortal: true,
});

export default createBoard({
    name: 'Warning',
    Board: () => {
        return (
            <Button
                intent="warning"
                onClick={() =>
                    toaster.show({
                        action: {
                            text: <strong>Adieu</strong>,
                        },
                        icon: 'hand',
                        intent: 'warning',
                        message: 'Goodbye, old friend.',
                    })
                }
            >
                Toast
            </Button>
        );
    },
    tags: ['toast', 'warning'],
    cover,
});
